import{R as a}from"./index-BS11OSAT.js";import{B as s}from"./BaseModule-mimRKdMN.js";const o=()=>{const e={title:"Gestión de Contraseñas",description:"Aprende las mejores prácticas para crear y gestionar contraseñas seguras en el entorno empresarial.",moduleId:2,prevModuleId:1,nextModuleId:3,sections:[{id:"password-basics",title:"Fundamentos de Contraseñas Seguras",content:"Una contraseña segura es la primera línea de defensa contra accesos no autorizados. Es fundamental entender los principios básicos de creación de contraseñas robustas.",points:["Longitud mínima de 12 caracteres","Combinación de mayúsculas, minúsculas, números y símbolos","Evitar información personal o fácil de adivinar","No reutilizar contraseñas en diferentes servicios"]},{id:"password-management",title:"Gestión de Contraseñas",content:"La gestión efectiva de contraseñas es crucial para mantener la seguridad de las cuentas empresariales. Esto incluye el uso de gestores de contraseñas y políticas de actualización regular.",points:["Uso de gestores de contraseñas","Actualización periódica de contraseñas","Autenticación de dos factores","Políticas de contraseñas corporativas"]},{id:"best-practices",title:"Mejores Prácticas",content:"Implementar buenas prácticas en la gestión de contraseñas puede prevenir la mayoría de los incidentes de seguridad relacionados con accesos no autorizados.",points:["No compartir contraseñas","Cerrar sesión en dispositivos compartidos","Reportar contraseñas comprometidas","Capacitación regular del personal"]}],videos:[{title:"Creación de Contraseñas Seguras",url:"https://www.youtube.com/embed/your-video-id-3"},{title:"Gestión de Contraseñas Empresariales",url:"https://www.youtube.com/embed/your-video-id-4"}],quiz:{questions:[{type:"multiple_choice",question:"¿Cuál es la longitud mínima recomendada para una contraseña segura?",answers:["12 caracteres","8 caracteres","6 caracteres","4 caracteres"],correctAnswer:0,correctFeedback:"¡Correcto! Una contraseña de 12 caracteres es más segura.",incorrectFeedback:"Incorrecto. Se recomienda una longitud mínima de 12 caracteres."},{type:"true_false",question:"Es seguro usar la misma contraseña para múltiples cuentas.",correctAnswer:!1,correctFeedback:"¡Correcto! Cada cuenta debe tener una contraseña única.",incorrectFeedback:"Incorrecto. Usar la misma contraseña en múltiples cuentas aumenta el riesgo de seguridad."},{type:"matching",question:"Relaciona cada característica con su importancia en la seguridad de contraseñas:",items:[{left:"Longitud"},{left:"Complejidad"},{left:"Unicidad"},{left:"Actualización"}],options:["Aumenta el tiempo necesario para descifrar","Dificulta la predicción y el ataque por fuerza bruta","Reduce el impacto si una contraseña es comprometida","Mitiga el riesgo de contraseñas comprometidas"],correctAnswer:[0,1,2,3],feedback:"Cada característica contribuye a la seguridad general de la contraseña."},{type:"multiple_choice",question:"¿Qué es un gestor de contraseñas?",answers:["Una herramienta que almacena y gestiona contraseñas de forma segura","Un programa que genera contraseñas aleatorias","Un sistema de recuperación de contraseñas","Un tipo de autenticación biométrica"],correctAnswer:0,correctFeedback:"¡Correcto! Un gestor de contraseñas almacena y gestiona contraseñas de forma segura.",incorrectFeedback:"Incorrecto. Un gestor de contraseñas es una herramienta que almacena y gestiona contraseñas de forma segura."},{type:"fill_blank",question:"Una contraseña fuerte debe incluir letras mayúsculas, minúsculas, números y ________.",correctAnswer:"símbolos",feedback:"Los símbolos especiales aumentan significativamente la complejidad de la contraseña."},{type:"multiple_choice",question:"¿Con qué frecuencia se recomienda cambiar las contraseñas?",answers:["Cada 3-6 meses","Una vez al año","Solo cuando hay una sospecha de compromiso","Nunca, una vez establecida"],correctAnswer:0,correctFeedback:"¡Correcto! Se recomienda cambiar las contraseñas cada 3-6 meses.",incorrectFeedback:"Incorrecto. Se recomienda cambiar las contraseñas cada 3-6 meses."},{type:"true_false",question:"Es seguro compartir contraseñas con colegas de confianza.",correctAnswer:!1,correctFeedback:"¡Correcto! Las contraseñas nunca deben compartirse, incluso con colegas de confianza.",incorrectFeedback:"Incorrecto. Las contraseñas son personales y no deben compartirse."},{type:"multiple_choice",question:"¿Qué es la autenticación de dos factores (2FA)?",answers:["Un método que requiere una contraseña y un segundo factor de verificación","Un sistema que usa dos contraseñas diferentes","Un método que requiere cambiar la contraseña dos veces","Un sistema de respaldo de contraseñas"],correctAnswer:0,correctFeedback:"¡Correcto! La 2FA requiere una contraseña y un segundo factor de verificación.",incorrectFeedback:"Incorrecto. La 2FA requiere una contraseña y un segundo factor de verificación."}]}};return a.createElement(s,{...e})};export{o as default};
